contracts/aggregation/OmniRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title OmniRouter (CrossChainRouter)
 * @notice Agregador cross-chain para encontrar mejores precios
 * @dev Track 5: Price comparison + routing via CCIP y LayerZero
 * @dev Usa BNB nativo en lugar de tokens ERC20
 */
contract OmniRouter is Ownable, ReentrancyGuard {
    // ============ State Variables ============
    
    address public coreContract;
    
    // Supported chains
    mapping(uint256 => ChainConfig) public chains;
    uint256[] public supportedChainIds;
    
    // Price oracle for each market across chains
    mapping(bytes32 => mapping(uint256 => MarketPrice)) public prices;
    // bytes32 = keccak256(abi.encode(marketQuestion))
    
    // Cross-chain execution tracking
    mapping(bytes32 => CrossChainBet) public pendingBets;
    mapping(address => bytes32[]) public userPendingBets;
    
    struct ChainConfig {
        bool supported;
        address remoteContract;
        uint256 gasLimit;
        uint256 baseFee;
    }
    
    struct MarketPrice {
        uint256 yesPrice; // scaled by 1e18
        uint256 noPrice;
        uint256 liquidity;
        uint256 lastUpdate;
        address marketAddress;
    }
    
    struct CrossChainBet {
        address user;
        uint256 sourceChainId;
        uint256 targetChainId;
        bytes32 marketHash;
        bool isYes;
        uint256 amount;
        uint256 expectedPrice;
        uint256 timestamp;
        BetStatus status;
    }
    
    enum BetStatus { Pending, Executed, Failed, Cancelled }
    
    // ============ Events ============
    
    event ChainAdded(
        uint256 indexed chainId,
        address remoteContract
    );
    
    event PriceUpdated(
        bytes32 indexed marketHash,
        uint256 indexed chainId,
        uint256 yesPrice,
        uint256 noPrice,
        uint256 liquidity
    );
    
    event CrossChainBetInitiated(
        bytes32 indexed betId,
        address indexed user,
        uint256 sourceChain,
        uint256 targetChain,
        uint256 amount
    );
    
    event CrossChainBetExecuted(
        bytes32 indexed betId,
        uint256 executionPrice,
        uint256 shares
    );
    
    event CrossChainBetFailed(
        bytes32 indexed betId,
        string reason
    );
    
    event ArbitrageOpportunity(
        bytes32 indexed marketHash,
        uint256 chain1,
        uint256 chain2,
        uint256 priceDiff
    );
    
    // ============ Constructor ============
    
    constructor() Ownable(msg.sender) {
    }
    
    // ============ Admin Functions ============
    
    function setCoreContract(address _core) external onlyOwner {
        coreContract = _core;
    }
    
    /**
     * @notice Agrega nueva chain soportada
     */
    function addChain(
        uint256 _chainId,
        address _remoteContract,
        uint256 _gasLimit,
        uint256 _baseFee
    ) external onlyOwner {
        chains[_chainId] = ChainConfig({
            supported: true,
            remoteContract: _remoteContract,
            gasLimit: _gasLimit,
            baseFee: _baseFee
        });
        
        supportedChainIds.push(_chainId);
        
        emit ChainAdded(_chainId, _remoteContract);
    }
    
    // ============ Price Aggregation Functions ============
    
    /**
     * @notice Actualiza precio de mercado en una chain
     */
    function updatePrice(
        string calldata _marketQuestion,
        uint256 _chainId,
        uint256 _yesPrice,
        uint256 _noPrice,
        uint256 _liquidity,
        address _marketAddress
    ) external {
        // Only callable by authorized oracles or cross-chain message
        bytes32 marketHash = keccak256(abi.encode(_marketQuestion));
        
        prices[marketHash][_chainId] = MarketPrice({
            yesPrice: _yesPrice,
            noPrice: _noPrice,
            liquidity: _liquidity,
            lastUpdate: block.timestamp,
            marketAddress: _marketAddress
        });
        
        emit PriceUpdated(
            marketHash,
            _chainId,
            _yesPrice,
            _noPrice,
            _liquidity
        );
        
        // Check for arbitrage opportunities
        _checkArbitrage(marketHash, _chainId);
    }
    
    /**
     * @notice Encuentra mejor precio entre chains
     */
    function findBestPrice(
        string calldata _marketQuestion,
        bool _isYes,
        uint256 _amount
    ) external view returns (
        uint256 bestChainId,
        uint256 bestPrice,
        uint256 estimatedShares,
        uint256 gasCost
    ) {
        bytes32 marketHash = keccak256(abi.encode(_marketQuestion));
        
        bestPrice = type(uint256).max;
        
        for (uint256 i = 0; i < supportedChainIds.length; i++) {
            uint256 chainId = supportedChainIds[i];
            MarketPrice storage price = prices[marketHash][chainId];
            
            if (price.lastUpdate == 0) continue;
            if (block.timestamp > price.lastUpdate + 5 minutes) continue;
            if (price.liquidity < _amount) continue;
            
            uint256 currentPrice = _isYes ? price.yesPrice : price.noPrice;
            uint256 totalCost = currentPrice + chains[chainId].baseFee;
            
            if (totalCost < bestPrice) {
                bestPrice = currentPrice;
                bestChainId = chainId;
                estimatedShares = (_amount * 1e18) / currentPrice;
                gasCost = chains[chainId].baseFee;
            }
        }
        
        require(bestPrice != type(uint256).max, "No liquidity available");
    }
    
    // ============ Cross-Chain Execution ============
    
    /**
     * @notice Rutea apuesta a mejor chain
     */
    function routeBet(
        uint256 _marketId,
        address _user,
        bool _isYes,
        uint256 _amount,
        uint256 _targetChainId
    ) external payable nonReentrant {
        require(msg.sender == coreContract, "Only core");
        require(chains[_targetChainId].supported, "Chain not supported");
        require(msg.value >= chains[_targetChainId].baseFee + _amount, "Insufficient BNB");
        
        // Generate bet ID
        bytes32 betId = keccak256(abi.encodePacked(
            _user,
            _marketId,
            _targetChainId,
            block.timestamp
        ));
        
        // Store pending bet
        pendingBets[betId] = CrossChainBet({
            user: _user,
            sourceChainId: block.chainid,
            targetChainId: _targetChainId,
            marketHash: keccak256(abi.encode(_marketId)),
            isYes: _isYes,
            amount: _amount,
            expectedPrice: 0,
            timestamp: block.timestamp,
            status: BetStatus.Pending
        });
        
        userPendingBets[_user].push(betId);
        
        // In production: Send cross-chain message via Chainlink CCIP or LayerZero
        
        emit CrossChainBetInitiated(
            betId,
            _user,
            block.chainid,
            _targetChainId,
            _amount
        );
    }
    
    /**
     * @notice Ejecuta apuesta cross-chain localmente
     */
    function executeCrossChainBet(
        bytes32 _betId,
        uint256 _marketId,
        bool _isYes,
        uint256 _amount,
        address _user
    ) external {
        require(msg.sender == coreContract, "Only core");
        
        CrossChainBet storage bet = pendingBets[_betId];
        require(bet.status == BetStatus.Pending, "Not pending");
        
        // Forward to core contract for execution with BNB
        // IPredictionMarketCore(coreContract).placeBet{value: _amount}(_marketId, _isYes);
        
        bet.status = BetStatus.Executed;
        
        emit CrossChainBetExecuted(_betId, 0, 0);
    }
    
    // Allow contract to receive BNB
    receive() external payable {}
    
    // ============ Arbitrage Detection ============
    
    /**
     * @notice Detecta oportunidades de arbitraje
     */
    function _checkArbitrage(bytes32 _marketHash, uint256 _chainId) internal {
        MarketPrice storage newPrice = prices[_marketHash][_chainId];
        
        for (uint256 i = 0; i < supportedChainIds.length; i++) {
            uint256 otherChainId = supportedChainIds[i];
            if (otherChainId == _chainId) continue;
            
            MarketPrice storage otherPrice = prices[_marketHash][otherChainId];
            if (otherPrice.lastUpdate == 0) continue;
            
            // Check YES price diff
            uint256 yesDiff = newPrice.yesPrice > otherPrice.yesPrice
                ? newPrice.yesPrice - otherPrice.yesPrice
                : otherPrice.yesPrice - newPrice.yesPrice;
            
            // Arbitrage if diff > 2%
            if (yesDiff * 100 / newPrice.yesPrice > 2) {
                emit ArbitrageOpportunity(
                    _marketHash,
                    _chainId,
                    otherChainId,
                    yesDiff
                );
            }
        }
    }
    
    // ============ View Functions ============
    
    function getSupportedChains() 
        external 
        view 
        returns (uint256[] memory) 
    {
        return supportedChainIds;
    }
    
    function getMarketPrices(string calldata _marketQuestion) 
        external 
        view 
        returns (
            uint256[] memory chainIds,
            uint256[] memory yesPrices,
            uint256[] memory noPrices,
            uint256[] memory liquidities
        ) 
    {
        bytes32 marketHash = keccak256(abi.encode(_marketQuestion));
        
        chainIds = new uint256[](supportedChainIds.length);
        yesPrices = new uint256[](supportedChainIds.length);
        noPrices = new uint256[](supportedChainIds.length);
        liquidities = new uint256[](supportedChainIds.length);
        
        for (uint256 i = 0; i < supportedChainIds.length; i++) {
            uint256 chainId = supportedChainIds[i];
            MarketPrice storage price = prices[marketHash][chainId];
            
            chainIds[i] = chainId;
            yesPrices[i] = price.yesPrice;
            noPrices[i] = price.noPrice;
            liquidities[i] = price.liquidity;
        }
    }
    
    function getPendingBet(bytes32 _betId) 
        external 
        view 
        returns (CrossChainBet memory) 
    {
        return pendingBets[_betId];
    }
    
    function getUserPendingBets(address _user) 
        external 
        view 
        returns (bytes32[] memory) 
    {
        return userPendingBets[_user];
    }
}

contracts/core/PredictionMarketCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../markets/BinaryMarket.sol";
import "../markets/ConditionalMarket.sol";
import "../markets/SubjectiveMarket.sol";
import "../oracle/AIOracle.sol";
import "../reputation/ReputationStaking.sol";
import "../oracle/InsurancePool.sol";
import "../aggregation/OmniRouter.sol";
import "../reputation/ReputationDAO.sol";
import "../governance/DAOGovernance.sol";

/**
 * @title PredictionMarketCore
 * @notice Main contract that orchestrates all MetaPredict.fun modules
 * @dev Integrates 5 tracks: AI Oracle, Reputation, Gasless UX, Conditional/Subjective, Aggregator
 * @dev Uses native BNB instead of ERC20 tokens
 */
contract PredictionMarketCore is Ownable, ReentrancyGuard, Pausable {
    // ============ Constants ============
    
    uint256 public constant VERSION = 1;
    uint256 public constant FEE_BASIS_POINTS = 50; // 0.5%
    uint256 public constant INSURANCE_FEE_BP = 10; // 0.1%
    uint256 public constant MIN_BET = 0.001 ether; // 0.001 BNB
    uint256 public constant MAX_BET = 100 ether; // 100 BNB
    
    // Module addresses
    BinaryMarket public binaryMarket;
    ConditionalMarket public conditionalMarket;
    SubjectiveMarket public subjectiveMarket;
    AIOracle public aiOracle;
    ReputationStaking public reputationStaking;
    InsurancePool public insurancePool;
    OmniRouter public crossChainRouter;
    DAOGovernance public daoGovernance;
    
    // Market registry
    uint256 public marketCounter;
    mapping(uint256 => MarketInfo) public markets;
    mapping(uint256 => address) public marketTypeContract;
    mapping(address => uint256[]) public userMarkets;
    
    // ============ Structs ============
    
    struct MarketInfo {
        uint256 id;
        MarketType marketType;
        address creator;
        uint256 createdAt;
        uint256 resolutionTime;
        MarketStatus status;
        string metadata; // IPFS hash
    }
    
    enum MarketType { Binary, Conditional, Subjective }
    enum MarketStatus { Active, Resolving, Resolved, Disputed, Cancelled }
    
    // ============ Events ============
    
    event MarketCreated(
        uint256 indexed marketId,
        MarketType marketType,
        address indexed creator,
        uint256 resolutionTime
    );
    
    event ModuleUpdated(
        string moduleName,
        address indexed oldAddress,
        address indexed newAddress
    );
    
    event FeeCollected(
        uint256 indexed marketId,
        address indexed user,
        uint256 tradingFee,
        uint256 insuranceFee
    );
    
    // ============ Constructor ============
    
    constructor(
        address payable _binaryMarket,
        address payable _conditionalMarket,
        address payable _subjectiveMarket,
        address _aiOracle,
        address payable _reputationStaking,
        address payable _insurancePool,
        address payable _crossChainRouter,
        address _daoGovernance
    ) Ownable(msg.sender) {
        binaryMarket = BinaryMarket(_binaryMarket);
        conditionalMarket = ConditionalMarket(_conditionalMarket);
        subjectiveMarket = SubjectiveMarket(_subjectiveMarket);
        aiOracle = AIOracle(_aiOracle);
        reputationStaking = ReputationStaking(_reputationStaking);
        insurancePool = InsurancePool(_insurancePool);
        crossChainRouter = OmniRouter(_crossChainRouter);
        daoGovernance = DAOGovernance(_daoGovernance);
    }
    
    // ============ Market Creation ============
    
    /**
     * @notice Crea un mercado binario estándar
     */
    function createBinaryMarket(
        string calldata _question,
        string calldata _description,
        uint256 _resolutionTime,
        string calldata _metadata
    ) external whenNotPaused returns (uint256) {
        require(_resolutionTime > block.timestamp + 1 hours, "Invalid time");
        
        uint256 marketId = ++marketCounter;
        
        markets[marketId] = MarketInfo({
            id: marketId,
            marketType: MarketType.Binary,
            creator: msg.sender,
            createdAt: block.timestamp,
            resolutionTime: _resolutionTime,
            status: MarketStatus.Active,
            metadata: _metadata
        });
        
        // Create binary market in dedicated contract
        binaryMarket.createMarket(
            marketId,
            _question,
            _description,
            _resolutionTime,
            _metadata
        );
        
        marketTypeContract[marketId] = address(binaryMarket);
        userMarkets[msg.sender].push(marketId);
        
        emit MarketCreated(
            marketId,
            MarketType.Binary,
            msg.sender,
            _resolutionTime
        );
        
        return marketId;
    }
    
    /**
     * @notice Crea un mercado condicional (if-then)
     */
    function createConditionalMarket(
        uint256 _parentMarketId,
        string calldata _condition,
        string calldata _question,
        uint256 _resolutionTime,
        string calldata _metadata
    ) external whenNotPaused returns (uint256) {
        require(markets[_parentMarketId].id != 0, "Invalid parent");
        require(_resolutionTime > markets[_parentMarketId].resolutionTime, "Invalid time");
        
        uint256 marketId = ++marketCounter;
        
        markets[marketId] = MarketInfo({
            id: marketId,
            marketType: MarketType.Conditional,
            creator: msg.sender,
            createdAt: block.timestamp,
            resolutionTime: _resolutionTime,
            status: MarketStatus.Active,
            metadata: _metadata
        });
        
        conditionalMarket.createMarket(
            marketId,
            _parentMarketId,
            _condition,
            _question,
            _resolutionTime,
            _metadata
        );
        
        marketTypeContract[marketId] = address(conditionalMarket);
        userMarkets[msg.sender].push(marketId);
        
        emit MarketCreated(
            marketId,
            MarketType.Conditional,
            msg.sender,
            _resolutionTime
        );
        
        return marketId;
    }
    
    /**
     * @notice Crea un mercado subjetivo (requiere DAO voting)
     */
    function createSubjectiveMarket(
        string calldata _question,
        string calldata _description,
        uint256 _resolutionTime,
        string calldata _expertiseRequired,
        string calldata _metadata
    ) external whenNotPaused returns (uint256) {
        require(_resolutionTime > block.timestamp + 1 days, "Min 1 day");
        
        uint256 marketId = ++marketCounter;
        
        markets[marketId] = MarketInfo({
            id: marketId,
            marketType: MarketType.Subjective,
            creator: msg.sender,
            createdAt: block.timestamp,
            resolutionTime: _resolutionTime,
            status: MarketStatus.Active,
            metadata: _metadata
        });
        
        subjectiveMarket.createMarket(
            marketId,
            _question,
            _description,
            _resolutionTime,
            _expertiseRequired,
            _metadata
        );
        
        marketTypeContract[marketId] = address(subjectiveMarket);
        userMarkets[msg.sender].push(marketId);
        
        emit MarketCreated(
            marketId,
            MarketType.Subjective,
            msg.sender,
            _resolutionTime
        );
        
        return marketId;
    }
    
    // ============ Betting Functions ============
    
    /**
     * @notice Coloca apuesta con BNB nativo
     */
    function placeBet(
        uint256 _marketId,
        bool _isYes
    ) external payable nonReentrant whenNotPaused {
        MarketInfo storage market = markets[_marketId];
        require(market.status == MarketStatus.Active, "Not active");
        require(msg.value >= MIN_BET && msg.value <= MAX_BET, "Invalid amount");
        
        uint256 _amount = msg.value;
        
        // Calculate fees
        uint256 tradingFee = (_amount * FEE_BASIS_POINTS) / 10000;
        uint256 insuranceFee = (_amount * INSURANCE_FEE_BP) / 10000;
        uint256 netAmount = _amount - tradingFee - insuranceFee;
        
        // Transfer to insurance pool
        insurancePool.receiveInsurancePremium{value: insuranceFee}(_marketId, insuranceFee);
        
        // Route to appropriate market contract
        address marketContract = marketTypeContract[_marketId];
        
        if (market.marketType == MarketType.Binary) {
            binaryMarket.placeBet{value: netAmount}(_marketId, msg.sender, _isYes, netAmount);
        } else if (market.marketType == MarketType.Conditional) {
            conditionalMarket.placeBet{value: netAmount}(_marketId, msg.sender, _isYes, netAmount);
        } else {
            subjectiveMarket.placeBet{value: netAmount}(_marketId, msg.sender, _isYes, netAmount);
        }
        
        emit FeeCollected(_marketId, msg.sender, tradingFee, insuranceFee);
    }
    
    // ============ Resolution Functions ============
    
    /**
     * @notice Inicia resolución (AI oracle o DAO)
     */
    function initiateResolution(uint256 _marketId) external {
        MarketInfo storage market = markets[_marketId];
        require(market.status == MarketStatus.Active, "Not active");
        require(block.timestamp >= market.resolutionTime, "Not ready");
        
        market.status = MarketStatus.Resolving;
        
        if (market.marketType == MarketType.Subjective) {
            // DAO voting para markets subjetivos
            daoGovernance.initiateVoting(_marketId);
        } else {
            // AI oracle para binary y conditional
            // TODO: Store question and pythPriceId in MarketInfo struct
            aiOracle.requestResolution(
                _marketId,
                "", // question - TODO: retrieve from market contract
                0   // pythPriceId - TODO: retrieve from market contract
            );
        }
    }
    
    /**
     * @notice Callback de resolución
     */
    function resolveMarket(
        uint256 _marketId,
        uint8 _outcome,
        uint8 _confidence
    ) external {
        require(
            msg.sender == address(aiOracle) || 
            msg.sender == address(daoGovernance),
            "Only oracle/DAO"
        );
        
        MarketInfo storage market = markets[_marketId];
        require(market.status == MarketStatus.Resolving, "Not resolving");
        
        // Si confidence < 80%, activar insurance
        if (_confidence < 80) {
            market.status = MarketStatus.Disputed;
            insurancePool.activateInsurance(_marketId);
            return;
        }
        
        market.status = MarketStatus.Resolved;
        
        // Route resolution to market contract
        address marketContract = marketTypeContract[_marketId];
        if (market.marketType == MarketType.Binary) {
            binaryMarket.resolveMarket(_marketId, _outcome);
        } else if (market.marketType == MarketType.Conditional) {
            conditionalMarket.resolveMarket(_marketId, _outcome);
        } else {
            subjectiveMarket.resolveMarket(_marketId, _outcome);
        }
    }
    
    // ============ Cross-Chain Functions ============
    
    /**
     * @notice Rutea apuesta a mejor chain (aggregator)
     */
    function placeBetCrossChain(
        uint256 _marketId,
        bool _isYes,
        uint256 _targetChainId
    ) external payable nonReentrant whenNotPaused {
        require(msg.value >= MIN_BET && msg.value <= MAX_BET, "Invalid amount");
        // Route via CrossChainRouter
        crossChainRouter.routeBet{value: msg.value}(
            _marketId,
            msg.sender,
            _isYes,
            msg.value,
            _targetChainId
        );
    }
    
    // ============ Reputation Functions ============
    
    /**
     * @notice Stake para participar en disputes
     */
    function stakeReputation() external payable {
        require(msg.value > 0, "Amount must be > 0");
        reputationStaking.stake{value: msg.value}(msg.sender, msg.value);
    }
    
    /**
     * @notice Vota en dispute
     */
    function voteOnDispute(
        uint256 _marketId,
        uint8 _vote
    ) external {
        reputationStaking.recordVote(msg.sender, _marketId, _vote);
    }
    
    // ============ Admin Functions ============
    
    function updateModule(
        string calldata _moduleName,
        address payable _newAddress
    ) external onlyOwner {
        address oldAddress;
        
        if (keccak256(bytes(_moduleName)) == keccak256("binaryMarket")) {
            oldAddress = address(binaryMarket);
            binaryMarket = BinaryMarket(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("conditionalMarket")) {
            oldAddress = address(conditionalMarket);
            conditionalMarket = ConditionalMarket(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("subjectiveMarket")) {
            oldAddress = address(subjectiveMarket);
            subjectiveMarket = SubjectiveMarket(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("aiOracle")) {
            oldAddress = address(aiOracle);
            aiOracle = AIOracle(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("reputationStaking")) {
            oldAddress = address(reputationStaking);
            reputationStaking = ReputationStaking(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("insurancePool")) {
            oldAddress = address(insurancePool);
            insurancePool = InsurancePool(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("crossChainRouter")) {
            oldAddress = address(crossChainRouter);
            crossChainRouter = OmniRouter(_newAddress);
        } else if (keccak256(bytes(_moduleName)) == keccak256("daoGovernance")) {
            oldAddress = address(daoGovernance);
            daoGovernance = DAOGovernance(_newAddress);
        } else {
            revert("Invalid module");
        }
        
        emit ModuleUpdated(_moduleName, oldAddress, _newAddress);
    }
    
    function pause() external onlyOwner {
        _pause();
    }
    
    function unpause() external onlyOwner {
        _unpause();
    }
    
    function emergencyWithdraw(uint256 _amount) 
        external 
        onlyOwner 
    {
        require(address(this).balance >= _amount, "Insufficient balance");
        (bool success, ) = payable(owner()).call{value: _amount}("");
        require(success, "Transfer failed");
    }
    
    // Allow contract to receive BNB
    receive() external payable {}
    
    // ============ View Functions ============
    
    function getMarket(uint256 _marketId) 
        external 
        view 
        returns (MarketInfo memory) 
    {
        return markets[_marketId];
    }
    
    function getUserMarkets(address _user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userMarkets[_user];
    }
    
    function getMarketContract(uint256 _marketId) 
        external 
        view 
        returns (address) 
    {
        return marketTypeContract[_marketId];
    }
}

contracts/governance/DAOGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title DAOGovernance
 * @notice Governanza descentralizada con quadratic voting y expertise validation
 * @dev Resuelve mercados subjetivos + propuestas de protocolo
 * @dev Usa BNB nativo en lugar de tokens ERC20
 */
contract DAOGovernance is Ownable, ReentrancyGuard {
    // ============ State Variables ============
    
    address public coreContract;
    address public reputationContract;
    
    uint256 public proposalCounter;
    uint256 public votingPeriod = 3 days;
    uint256 public minQuorum = 1 ether; // 1 BNB
    uint256 public expertiseWeight = 2; // 2x multiplier
    
    // Proposal types
    enum ProposalType { 
        MarketResolution,  // Resolve subjective market
        ParameterChange,   // Change protocol parameters
        TreasurySpend,     // Spend treasury funds
        EmergencyAction    // Emergency pause/unpause
    }
    
    enum ProposalStatus {
        Pending,
        Active,
        Succeeded,
        Defeated,
        Executed,
        Cancelled
    }
    
    struct Proposal {
        uint256 id;
        ProposalType proposalType;
        address proposer;
        string title;
        string description;
        uint256 marketId; // For market resolution proposals
        uint256 startBlock;
        uint256 endBlock;
        uint256 forVotes;
        uint256 againstVotes;
        uint256 abstainVotes;
        ProposalStatus status;
        bool executed;
        mapping(address => Vote) votes;
    }
    
    struct Vote {
        bool hasVoted;
        uint8 support; // 0=Against, 1=For, 2=Abstain
        uint256 votes;
        uint256 quadraticVotes;
        bool isExpert;
    }
    
    struct Expertise {
        string domain; // "film", "sports", "economics", etc.
        uint256 score;
        bool verified;
        uint256 verifiedAt;
        address[] attestations;
    }
    
    mapping(uint256 => Proposal) public proposals;
    mapping(address => mapping(string => Expertise)) public expertises;
    mapping(address => uint256[]) public userProposals;
    mapping(address => uint256[]) public userVotes;
    
    // ============ Events ============
    
    event ProposalCreated(
        uint256 indexed proposalId,
        address indexed proposer,
        ProposalType proposalType,
        string title
    );
    
    event VoteCast(
        address indexed voter,
        uint256 indexed proposalId,
        uint8 support,
        uint256 votes,
        uint256 quadraticVotes,
        bool isExpert
    );
    
    event ProposalExecuted(
        uint256 indexed proposalId,
        ProposalStatus status
    );
    
    event ExpertiseVerified(
        address indexed expert,
        string domain,
        uint256 score
    );
    
    event ExpertiseAttested(
        address indexed expert,
        address indexed attester,
        string domain
    );
    
    // ============ Constructor ============
    
    constructor(
        address _reputationContract
    ) Ownable(msg.sender) {
        reputationContract = _reputationContract;
    }
    
    // ============ Modifiers ============
    
    modifier onlyCore() {
        require(msg.sender == coreContract, "Only core");
        _;
    }
    
    // ============ Admin Functions ============
    
    function setCoreContract(address _core) external onlyOwner {
        coreContract = _core;
    }
    
    function setVotingPeriod(uint256 _period) external onlyOwner {
        votingPeriod = _period;
    }
    
    function setMinQuorum(uint256 _quorum) external onlyOwner {
        minQuorum = _quorum;
    }
    
    // ============ Proposal Creation ============
    
    /**
     * @notice Crea propuesta de resolución de mercado subjetivo
     */
    function initiateVoting(uint256 _marketId) external onlyCore returns (uint256) {
        uint256 proposalId = ++proposalCounter;
        
        Proposal storage proposal = proposals[proposalId];
        proposal.id = proposalId;
        proposal.proposalType = ProposalType.MarketResolution;
        proposal.proposer = msg.sender;
        proposal.marketId = _marketId;
        proposal.startBlock = block.number;
        proposal.endBlock = block.number + (votingPeriod / 12); // ~12s blocks
        proposal.status = ProposalStatus.Active;
        
        emit ProposalCreated(
            proposalId,
            msg.sender,
            ProposalType.MarketResolution,
            "Market Resolution"
        );
        
        return proposalId;
    }
    
    /**
     * @notice Crea propuesta de cambio de parámetros
     */
    function createParameterProposal(
        string calldata _title,
        string calldata _description
    ) external payable returns (uint256) {
        require(
            msg.value >= 0.1 ether,
            "Insufficient BNB (min 0.1 BNB)"
        );
        
        uint256 proposalId = ++proposalCounter;
        
        Proposal storage proposal = proposals[proposalId];
        proposal.id = proposalId;
        proposal.proposalType = ProposalType.ParameterChange;
        proposal.proposer = msg.sender;
        proposal.title = _title;
        proposal.description = _description;
        proposal.startBlock = block.number;
        proposal.endBlock = block.number + (votingPeriod / 12);
        proposal.status = ProposalStatus.Active;
        
        userProposals[msg.sender].push(proposalId);
        
        emit ProposalCreated(
            proposalId,
            msg.sender,
            ProposalType.ParameterChange,
            _title
        );
        
        return proposalId;
    }
    
    // ============ Voting Functions ============
    
    /**
     * @notice Vota con quadratic voting + expertise boost
     */
    function castVote(
        uint256 _proposalId,
        uint8 _support,
        string calldata _expertiseDomain
    ) external nonReentrant {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.status == ProposalStatus.Active, "Not active");
        require(block.number <= proposal.endBlock, "Voting ended");
        require(!proposal.votes[msg.sender].hasVoted, "Already voted");
        require(_support <= 2, "Invalid support value");
        
        // Voting power based on BNB balance in contract (staked via reputation)
        // In production, this would query ReputationStaking contract
        uint256 votes = address(msg.sender).balance; // Simplified - should query reputation staking
        require(votes > 0, "No voting power");
        
        // Quadratic voting: sqrt of token balance
        uint256 quadraticVotes = _sqrt(votes);
        
        // Check expertise
        bool isExpert = false;
        if (bytes(_expertiseDomain).length > 0) {
            Expertise storage expertise = expertises[msg.sender][_expertiseDomain];
            if (expertise.verified && expertise.score >= 70) {
                isExpert = true;
                quadraticVotes = quadraticVotes * expertiseWeight;
            }
        }
        
        // Record vote
        proposal.votes[msg.sender] = Vote({
            hasVoted: true,
            support: _support,
            votes: votes,
            quadraticVotes: quadraticVotes,
            isExpert: isExpert
        });
        
        // Update tallies
        if (_support == 0) {
            proposal.againstVotes += quadraticVotes;
        } else if (_support == 1) {
            proposal.forVotes += quadraticVotes;
        } else {
            proposal.abstainVotes += quadraticVotes;
        }
        
        userVotes[msg.sender].push(_proposalId);
        
        emit VoteCast(
            msg.sender,
            _proposalId,
            _support,
            votes,
            quadraticVotes,
            isExpert
        );
    }
    
    // ============ Proposal Execution ============
    
    /**
     * @notice Ejecuta propuesta aprobada
     */
    function executeProposal(uint256 _proposalId) external nonReentrant {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.status == ProposalStatus.Active, "Not active");
        require(block.number > proposal.endBlock, "Voting not ended");
        require(!proposal.executed, "Already executed");
        
        uint256 totalVotes = proposal.forVotes + proposal.againstVotes;
        require(totalVotes >= minQuorum, "Quorum not reached");
        
        // Determine outcome
        if (proposal.forVotes > proposal.againstVotes) {
            proposal.status = ProposalStatus.Succeeded;
            
            // Execute based on type
            if (proposal.proposalType == ProposalType.MarketResolution) {
                _executeMarketResolution(proposal);
            }
            
            proposal.executed = true;
        } else {
            proposal.status = ProposalStatus.Defeated;
        }
        
        emit ProposalExecuted(_proposalId, proposal.status);
    }
    
    function _executeMarketResolution(Proposal storage proposal) internal {
        // Determine outcome: 1=Yes if forVotes > 2/3, 2=No if againstVotes > 2/3, else 3=Invalid
        uint256 total = proposal.forVotes + proposal.againstVotes + proposal.abstainVotes;
        uint8 outcome;
        
        if (proposal.forVotes * 3 > total * 2) {
            outcome = 1; // Yes
        } else if (proposal.againstVotes * 3 > total * 2) {
            outcome = 2; // No
        } else {
            outcome = 3; // Invalid
        }
        
        // Notify core contract
        IPredictionMarketCore(coreContract).resolveMarket(
            proposal.marketId,
            outcome,
            95 // High confidence from DAO
        );
    }
    
    // ============ Expertise Management ============
    
    /**
     * @notice Registra expertise en un dominio
     */
    function registerExpertise(
        string calldata _domain,
        string calldata _evidence
    ) external {
        Expertise storage expertise = expertises[msg.sender][_domain];
        require(!expertise.verified, "Already verified");
        
        expertise.domain = _domain;
        expertise.score = 50; // Base score
        expertise.verified = false;
    }
    
    /**
     * @notice Atesta expertise de otro usuario
     */
    function attestExpertise(
        address _expert,
        string calldata _domain
    ) external {
        Expertise storage myExpertise = expertises[msg.sender][_domain];
        require(myExpertise.verified, "Not verified yourself");
        require(myExpertise.score >= 70, "Insufficient score");
        
        Expertise storage expertExpertise = expertises[_expert][_domain];
        require(bytes(expertExpertise.domain).length > 0, "Not registered");
        
        expertExpertise.attestations.push(msg.sender);
        expertExpertise.score += 5;
        
        // Auto-verify if 3+ attestations
        if (expertExpertise.attestations.length >= 3 && !expertExpertise.verified) {
            expertExpertise.verified = true;
            expertExpertise.verifiedAt = block.timestamp;
            
            emit ExpertiseVerified(_expert, _domain, expertExpertise.score);
        }
        
        emit ExpertiseAttested(_expert, msg.sender, _domain);
    }
    
    /**
     * @notice Verifica expertise manualmente (owner only)
     */
    function verifyExpertise(
        address _expert,
        string calldata _domain,
        uint256 _score
    ) external onlyOwner {
        Expertise storage expertise = expertises[_expert][_domain];
        expertise.verified = true;
        expertise.verifiedAt = block.timestamp;
        expertise.score = _score;
        
        emit ExpertiseVerified(_expert, _domain, _score);
    }
    
    // ============ View Functions ============
    
    function getProposal(uint256 _proposalId) 
        external 
        view 
        returns (
            uint256 id,
            ProposalType proposalType,
            address proposer,
            string memory title,
            string memory description,
            uint256 forVotes,
            uint256 againstVotes,
            uint256 abstainVotes,
            ProposalStatus status,
            bool executed
        ) 
    {
        Proposal storage proposal = proposals[_proposalId];
        return (
            proposal.id,
            proposal.proposalType,
            proposal.proposer,
            proposal.title,
            proposal.description,
            proposal.forVotes,
            proposal.againstVotes,
            proposal.abstainVotes,
            proposal.status,
            proposal.executed
        );
    }
    
    function getVote(uint256 _proposalId, address _voter) 
        external 
        view 
        returns (Vote memory) 
    {
        return proposals[_proposalId].votes[_voter];
    }
    
    function getUserProposals(address _user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userProposals[_user];
    }
    
    function getUserVotes(address _user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userVotes[_user];
    }
    
    function getExpertise(address _user, string calldata _domain) 
        external 
        view 
        returns (Expertise memory) 
    {
        return expertises[_user][_domain];
    }
    
    // ============ Internal Functions ============
    
    function _sqrt(uint256 x) internal pure returns (uint256 y) {
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }
}

interface IPredictionMarketCore {
    function resolveMarket(uint256 _marketId, uint8 _outcome, uint8 _confidence) external;
}

