// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BinaryMarket
 * @notice Mercado de predicción binario estándar (YES/NO)
 * @dev Usa BNB nativo en lugar de tokens ERC20
 */
contract BinaryMarket is Ownable, ReentrancyGuard {
    address public immutable coreContract;
    
    struct Market {
        string question;
        string description;
        uint256 resolutionTime;
        uint256 totalYesShares;
        uint256 totalNoShares;
        uint256 yesPool;
        uint256 noPool;
        bool resolved;
        uint8 outcome; // 1=Yes, 2=No, 3=Invalid
    }
    
    struct Position {
        uint256 yesShares;
        uint256 noShares;
        uint256 avgYesPrice;
        uint256 avgNoPrice;
        bool claimed;
    }
    
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => Position)) public positions;
    
    event BetPlaced(
        uint256 indexed marketId,
        address indexed user,
        bool isYes,
        uint256 amount,
        uint256 shares
    );
    
    event MarketResolved(
        uint256 indexed marketId,
        uint8 outcome
    );
    
    event WinningsClaimed(
        uint256 indexed marketId,
        address indexed user,
        uint256 amount
    );
    
    constructor(address _coreContract) 
        Ownable(msg.sender) 
    {
        coreContract = _coreContract;
    }
    
    modifier onlyCore() {
        require(msg.sender == coreContract, "Only core");
        _;
    }
    
    function createMarket(
        uint256 _marketId,
        string calldata _question,
        string calldata _description,
        uint256 _resolutionTime,
        string calldata // _metadata unused here
    ) external onlyCore {
        markets[_marketId] = Market({
            question: _question,
            description: _description,
            resolutionTime: _resolutionTime,
            totalYesShares: 0,
            totalNoShares: 0,
            yesPool: 0,
            noPool: 0,
            resolved: false,
            outcome: 0
        });
    }
    
    function placeBet(
        uint256 _marketId,
        address _user,
        bool _isYes,
        uint256 _amount
    ) external payable onlyCore nonReentrant {
        Market storage market = markets[_marketId];
        require(!market.resolved, "Already resolved");
        require(msg.value == _amount, "Amount mismatch");
        
        // Calculate shares (constant product AMM)
        uint256 shares = _calculateShares(market, _isYes, _amount);
        uint256 avgPrice = (_amount * 1e18) / shares;
        
        if (_isYes) {
            market.yesPool += _amount;
            market.totalYesShares += shares;
            positions[_marketId][_user].yesShares += shares;
            positions[_marketId][_user].avgYesPrice = _calculateAvgPrice(
                positions[_marketId][_user].avgYesPrice,
                positions[_marketId][_user].yesShares - shares,
                avgPrice,
                shares
            );
        } else {
            market.noPool += _amount;
            market.totalNoShares += shares;
            positions[_marketId][_user].noShares += shares;
            positions[_marketId][_user].avgNoPrice = _calculateAvgPrice(
                positions[_marketId][_user].avgNoPrice,
                positions[_marketId][_user].noShares - shares,
                avgPrice,
                shares
            );
        }
        
        emit BetPlaced(_marketId, _user, _isYes, _amount, shares);
    }
    
    function resolveMarket(uint256 _marketId, uint8 _outcome) 
        external 
        onlyCore 
    {
        Market storage market = markets[_marketId];
        require(!market.resolved, "Already resolved");
        
        market.resolved = true;
        market.outcome = _outcome;
        
        emit MarketResolved(_marketId, _outcome);
    }
    
    function claimWinnings(uint256 _marketId) 
        external 
        nonReentrant 
    {
        Market storage market = markets[_marketId];
        require(market.resolved, "Not resolved");
        
        Position storage position = positions[_marketId][msg.sender];
        require(!position.claimed, "Already claimed");
        require(
            position.yesShares > 0 || position.noShares > 0,
            "No position"
        );
        
        uint256 payout = 0;
        
        if (market.outcome == 1 && position.yesShares > 0) {
            payout = (position.yesShares * (market.yesPool + market.noPool)) / 
                     market.totalYesShares;
        } else if (market.outcome == 2 && position.noShares > 0) {
            payout = (position.noShares * (market.yesPool + market.noPool)) / 
                     market.totalNoShares;
        } else if (market.outcome == 3) {
            uint256 yesInvested = (position.yesShares * position.avgYesPrice) / 1e18;
            uint256 noInvested = (position.noShares * position.avgNoPrice) / 1e18;
            payout = yesInvested + noInvested;
        }
        
        require(payout > 0, "No winnings");
        
        position.claimed = true;
        
        (bool success, ) = payable(msg.sender).call{value: payout}("");
        require(success, "Transfer failed");
        
        emit WinningsClaimed(_marketId, msg.sender, payout);
    }
    
    // Allow contract to receive BNB
    receive() external payable {}
    
    function _calculateShares(
        Market storage market,
        bool _isYes,
        uint256 _amount
    ) internal view returns (uint256) {
        uint256 pool = _isYes ? market.yesPool : market.noPool;
        uint256 totalShares = _isYes ? market.totalYesShares : market.totalNoShares;
        
        if (totalShares == 0) {
            return _amount;
        }
        
        return (_amount * totalShares) / pool;
    }
    
    function _calculateAvgPrice(
        uint256 _oldAvg,
        uint256 _oldShares,
        uint256 _newPrice,
        uint256 _newShares
    ) internal pure returns (uint256) {
        if (_oldShares == 0) return _newPrice;
        
        return ((_oldAvg * _oldShares) + (_newPrice * _newShares)) / 
               (_oldShares + _newShares);
    }
    
    function getMarket(uint256 _marketId) 
        external 
        view 
        returns (Market memory) 
    {
        return markets[_marketId];
    }
    
    function getPosition(uint256 _marketId, address _user) 
        external 
        view 
        returns (Position memory) 
    {
        return positions[_marketId][_user];
    }
    
    function getCurrentOdds(uint256 _marketId) 
        external 
        view 
        returns (uint256 yesOdds, uint256 noOdds) 
    {
        Market storage market = markets[_marketId];
        
        uint256 totalPool = market.yesPool + market.noPool;
        if (totalPool == 0) return (5000, 5000);
        
        yesOdds = (market.yesPool * 10000) / totalPool;
        noOdds = (market.noPool * 10000) / totalPool;
    }
}

contracts/markets/ConditionalMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ConditionalMarket
 * @notice Mercados condicionales en árbol (if-then predictions)
 * @dev Parent market debe resolverse antes que child markets
 * @dev Usa BNB nativo en lugar de tokens ERC20
 */
contract ConditionalMarket is Ownable, ReentrancyGuard {
    address public immutable coreContract;
    
    struct Market {
        uint256 parentMarketId;
        string condition; // "if YES on parent", "if NO on parent"
        string question;
        uint256 resolutionTime;
        uint256 totalYesShares;
        uint256 totalNoShares;
        uint256 yesPool;
        uint256 noPool;
        bool parentResolved;
        bool conditionMet;
        bool resolved;
        uint8 outcome;
    }
    
    struct Position {
        uint256 yesShares;
        uint256 noShares;
        uint256 avgYesPrice;
        uint256 avgNoPrice;
        bool claimed;
    }
    
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => Position)) public positions;
    mapping(uint256 => uint256[]) public childMarkets; // parent => children
    
    event ConditionalMarketCreated(
        uint256 indexed marketId,
        uint256 indexed parentMarketId,
        string condition
    );
    
    event ParentResolved(
        uint256 indexed marketId,
        bool conditionMet
    );
    
    event BetPlaced(
        uint256 indexed marketId,
        address indexed user,
        bool isYes,
        uint256 amount,
        uint256 shares
    );
    
    event MarketResolved(
        uint256 indexed marketId,
        uint8 outcome
    );
    
    constructor(address _coreContract) 
        Ownable(msg.sender) 
    {
        coreContract = _coreContract;
    }
    
    modifier onlyCore() {
        require(msg.sender == coreContract, "Only core");
        _;
    }
    
    function createMarket(
        uint256 _marketId,
        uint256 _parentMarketId,
        string calldata _condition,
        string calldata _question,
        uint256 _resolutionTime,
        string calldata // _metadata
    ) external onlyCore {
        markets[_marketId] = Market({
            parentMarketId: _parentMarketId,
            condition: _condition,
            question: _question,
            resolutionTime: _resolutionTime,
            totalYesShares: 0,
            totalNoShares: 0,
            yesPool: 0,
            noPool: 0,
            parentResolved: false,
            conditionMet: false,
            resolved: false,
            outcome: 0
        });
        
        childMarkets[_parentMarketId].push(_marketId);
        
        emit ConditionalMarketCreated(_marketId, _parentMarketId, _condition);
    }
    
    function placeBet(
        uint256 _marketId,
        address _user,
        bool _isYes,
        uint256 _amount
    ) external payable onlyCore nonReentrant {
        Market storage market = markets[_marketId];
        require(!market.resolved, "Already resolved");
        require(!market.parentResolved, "Awaiting parent");
        require(msg.value == _amount, "Amount mismatch");
        
        // Calculate shares
        uint256 shares = _calculateShares(market, _isYes, _amount);
        uint256 avgPrice = (_amount * 1e18) / shares;
        
        if (_isYes) {
            market.yesPool += _amount;
            market.totalYesShares += shares;
            positions[_marketId][_user].yesShares += shares;
            positions[_marketId][_user].avgYesPrice = _calculateAvgPrice(
                positions[_marketId][_user].avgYesPrice,
                positions[_marketId][_user].yesShares - shares,
                avgPrice,
                shares
            );
        } else {
            market.noPool += _amount;
            market.totalNoShares += shares;
            positions[_marketId][_user].noShares += shares;
            positions[_marketId][_user].avgNoPrice = _calculateAvgPrice(
                positions[_marketId][_user].avgNoPrice,
                positions[_marketId][_user].noShares - shares,
                avgPrice,
                shares
            );
        }
        
        emit BetPlaced(_marketId, _user, _isYes, _amount, shares);
    }
    
    /**
     * @notice Notifica cuando parent market se resuelve
     */
    function notifyParentResolution(
        uint256 _parentMarketId,
        uint8 _parentOutcome
    ) external onlyCore {
        uint256[] memory children = childMarkets[_parentMarketId];
        
        for (uint256 i = 0; i < children.length; i++) {
            uint256 childId = children[i];
            Market storage child = markets[childId];
            
            child.parentResolved = true;
            
            // Check if condition met
            if (
                (keccak256(bytes(child.condition)) == keccak256("if YES") && _parentOutcome == 1) ||
                (keccak256(bytes(child.condition)) == keccak256("if NO") && _parentOutcome == 2)
            ) {
                child.conditionMet = true;
            } else {
                // Condition not met: refund all bets
                child.resolved = true;
                child.outcome = 3; // Invalid
            }
            
            emit ParentResolved(childId, child.conditionMet);
        }
    }
    
    function resolveMarket(uint256 _marketId, uint8 _outcome) 
        external 
        onlyCore 
    {
        Market storage market = markets[_marketId];
        require(market.conditionMet, "Condition not met");
        require(!market.resolved, "Already resolved");
        
        market.resolved = true;
        market.outcome = _outcome;
        
        emit MarketResolved(_marketId, _outcome);
    }
    
    function claimWinnings(uint256 _marketId) 
        external 
        nonReentrant 
    {
        Market storage market = markets[_marketId];
        require(market.resolved, "Not resolved");
        
        Position storage position = positions[_marketId][msg.sender];
        require(!position.claimed, "Already claimed");
        
        uint256 payout = 0;
        
        if (market.outcome == 1 && position.yesShares > 0) {
            payout = (position.yesShares * (market.yesPool + market.noPool)) / 
                     market.totalYesShares;
        } else if (market.outcome == 2 && position.noShares > 0) {
            payout = (position.noShares * (market.yesPool + market.noPool)) / 
                     market.totalNoShares;
        } else if (market.outcome == 3 || !market.conditionMet) {
            // Refund
            uint256 yesInvested = (position.yesShares * position.avgYesPrice) / 1e18;
            uint256 noInvested = (position.noShares * position.avgNoPrice) / 1e18;
            payout = yesInvested + noInvested;
        }
        
        require(payout > 0, "No winnings");
        
        position.claimed = true;
        
        (bool success, ) = payable(msg.sender).call{value: payout}("");
        require(success, "Transfer failed");
    }
    
    // Allow contract to receive BNB
    receive() external payable {}
    
    function _calculateShares(
        Market storage market,
        bool _isYes,
        uint256 _amount
    ) internal view returns (uint256) {
        uint256 pool = _isYes ? market.yesPool : market.noPool;
        uint256 totalShares = _isYes ? market.totalYesShares : market.totalNoShares;
        
        if (totalShares == 0) return _amount;
        return (_amount * totalShares) / pool;
    }
    
    function _calculateAvgPrice(
        uint256 _oldAvg,
        uint256 _oldShares,
        uint256 _newPrice,
        uint256 _newShares
    ) internal pure returns (uint256) {
        if (_oldShares == 0) return _newPrice;
        return ((_oldAvg * _oldShares) + (_newPrice * _newShares)) / 
               (_oldShares + _newShares);
    }
    
    function getChildMarkets(uint256 _parentMarketId) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return childMarkets[_parentMarketId];
    }
    
    function getMarket(uint256 _marketId) 
        external 
        view 
        returns (Market memory) 
    {
        return markets[_marketId];
    }
    
    function getPosition(uint256 _marketId, address _user) 
        external 
        view 
        returns (Position memory) 
    {
        return positions[_marketId][_user];
    }
}

contracts/markets/PredictionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../oracle/AIOracle.sol";
import "../oracle/InsurancePool.sol";
import "../libraries/MarketMath.sol";
import "../libraries/Errors.sol";

/**
 * @title PredictionMarket
 * @notice Mercado de predicción con AI oracle y seguro contra manipulación
 * @dev Compatible con opBNB, optimizado para gas ultra-bajo
 */
contract PredictionMarket is Ownable, ReentrancyGuard, Pausable {
    // ============ State Variables ============
    
    IERC20 public immutable bettingToken; // USDC/USDT en opBNB
    AIOracle public immutable aiOracle;
    InsurancePool public immutable insurancePool;
    
    uint256 public marketCounter;
    uint256 public constant FEE_BASIS_POINTS = 50; // 0.5% fee
    uint256 public constant INSURANCE_FEE_BP = 10; // 0.1% insurance premium
    uint256 public constant MIN_BET = 1e6; // $1 minimum (6 decimals USDC)
    uint256 public constant MAX_BET = 10_000e6; // $10,000 max per bet
    
    // ============ Structs ============
    
    struct Market {
        uint256 id;
        string question;
        string description;
        address creator;
        uint256 createdAt;
        uint256 resolutionTime;
        uint256 totalYesShares;
        uint256 totalNoShares;
        uint256 yesPool;
        uint256 noPool;
        uint256 insuranceReserve;
        MarketStatus status;
        Outcome outcome;
        string metadata; // IPFS hash
        uint256 pythPriceId; // ✅ NEW: Pyth price ID para validación
    }
    
    struct Position {
        uint256 yesShares;
        uint256 noShares;
        uint256 avgYesPrice;
        uint256 avgNoPrice;
        bool claimed;
    }
    
    enum MarketStatus { Active, Resolving, Resolved, Disputed, Cancelled }
    enum Outcome { Pending, Yes, No, Invalid }
    
    // ============ Mappings ============
    
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => Position)) public positions;
    mapping(address => uint256[]) public userMarkets;
    
    // ============ Events ============
    
    event MarketCreated(
        uint256 indexed marketId,
        string question,
        address indexed creator,
        uint256 resolutionTime,
        uint256 pythPriceId
    );
    
    event BetPlaced(
        uint256 indexed marketId,
        address indexed user,
        bool isYes,
        uint256 amount,
        uint256 shares,
        uint256 price
    );
    
    event MarketResolved(
        uint256 indexed marketId,
        Outcome outcome,
        uint256 timestamp
    );
    
    event WinningsClaimed(
        uint256 indexed marketId,
        address indexed user,
        uint256 amount
    );
    
    event InsuranceClaimed(
        uint256 indexed marketId,
        address indexed user,
        uint256 amount
    );
    
    // ============ Constructor ============
    
    constructor(
        address _bettingToken,
        address _aiOracle,
        address payable _insurancePool
    ) Ownable(msg.sender) {
        if (_bettingToken == address(0)) revert Errors.InvalidAmount();
        if (_aiOracle == address(0)) revert Errors.InvalidAmount();
        if (_insurancePool == address(0)) revert Errors.InvalidAmount();
        
        bettingToken = IERC20(_bettingToken);
        aiOracle = AIOracle(payable(_aiOracle));
        insurancePool = InsurancePool(_insurancePool);
    }
    
    // ============ Market Creation ============
    
    /**
     * @notice Crea un nuevo mercado de predicción
     * @param _question Pregunta del mercado
     * @param _description Descripción detallada
     * @param _resolutionTime Timestamp cuando resuelve
     * @param _metadata IPFS hash con metadata adicional
     * @param _pythPriceId ID de precio Pyth para validación (0 = no validación)
     */
    function createMarket(
        string calldata _question,
        string calldata _description,
        uint256 _resolutionTime,
        string calldata _metadata,
        uint256 _pythPriceId
    ) external whenNotPaused returns (uint256) {
        // ✅ FIX #4: Custom errors en lugar de require
        if (_resolutionTime <= block.timestamp + 1 hours) revert Errors.InvalidTime();
        if (_resolutionTime > block.timestamp + 365 days) revert Errors.InvalidTime();
        if (bytes(_question).length <= 10) revert Errors.QuestionTooShort();
        
        uint256 marketId = ++marketCounter;
        
        markets[marketId] = Market({
            id: marketId,
            question: _question,
            description: _description,
            creator: msg.sender,
            createdAt: block.timestamp,
            resolutionTime: _resolutionTime,
            totalYesShares: 0,
            totalNoShares: 0,
            yesPool: 0,
            noPool: 0,
            insuranceReserve: 0,
            status: MarketStatus.Active,
            outcome: Outcome.Pending,
            metadata: _metadata,
            pythPriceId: _pythPriceId // ✅ NEW
        });
        
        userMarkets[msg.sender].push(marketId);
        
        emit MarketCreated(marketId, _question, msg.sender, _resolutionTime, _pythPriceId);
        
        return marketId;
    }
    
    // ============ Betting Functions ============
    
    /**
     * @notice Coloca una apuesta en el mercado
     * @param _marketId ID del mercado
     * @param _isYes true para apostar YES, false para NO
     * @param _amount Cantidad en USDC (6 decimals)
     */
    function placeBet(
        uint256 _marketId,
        bool _isYes,
        uint256 _amount
    ) external nonReentrant whenNotPaused {
        Market storage market = markets[_marketId];
        
        // ✅ FIX #4: Custom errors
        if (market.status != MarketStatus.Active) revert Errors.MarketNotActive();
        if (block.timestamp >= market.resolutionTime) revert Errors.MarketExpired();
        if (_amount < MIN_BET || _amount > MAX_BET) revert Errors.InvalidBetAmount();
        
        // Transfer tokens from user
        bool success = bettingToken.transferFrom(msg.sender, address(this), _amount);
        if (!success) revert Errors.TransferFailed();
        
        // ✅ FIX #4: Optimización de gas con unchecked
        uint256 tradingFee;
        uint256 insuranceFee;
        uint256 netAmount;
        
        unchecked {
            tradingFee = (_amount * FEE_BASIS_POINTS) / 10000;
            insuranceFee = (_amount * INSURANCE_FEE_BP) / 10000;
            netAmount = _amount - tradingFee - insuranceFee;
        }
        
        // Transfer insurance premium
        success = bettingToken.transfer(address(insurancePool), insuranceFee);
        if (!success) revert Errors.TransferFailed();
        
        market.insuranceReserve += insuranceFee;
        
        // Calculate shares using constant product formula
        uint256 shares = MarketMath.calculateShares(
            market.yesPool, 
            market.noPool, 
            market.totalYesShares, 
            market.totalNoShares, 
            _isYes, 
            netAmount
        );
        
        uint256 avgPrice;
        unchecked {
            avgPrice = (netAmount * 1e18) / shares;
        }
        
        // Update market state
        if (_isYes) {
            unchecked {
                market.yesPool += netAmount;
                market.totalYesShares += shares;
            }
            positions[_marketId][msg.sender].yesShares += shares;
            positions[_marketId][msg.sender].avgYesPrice = 
                MarketMath.calculateAvgPrice(
                    positions[_marketId][msg.sender].avgYesPrice,
                    positions[_marketId][msg.sender].yesShares - shares,
                    avgPrice,
                    shares
                );
        } else {
            unchecked {
                market.noPool += netAmount;
                market.totalNoShares += shares;
            }
            positions[_marketId][msg.sender].noShares += shares;
            positions[_marketId][msg.sender].avgNoPrice = 
                MarketMath.calculateAvgPrice(
                    positions[_marketId][msg.sender].avgNoPrice,
                    positions[_marketId][msg.sender].noShares - shares,
                    avgPrice,
                    shares
                );
        }
        
        // Track user markets
        if (positions[_marketId][msg.sender].yesShares + 
            positions[_marketId][msg.sender].noShares == shares) {
            userMarkets[msg.sender].push(_marketId);
        }
        
        emit BetPlaced(_marketId, msg.sender, _isYes, _amount, shares, avgPrice);
    }
    
    // ============ Resolution Functions ============
    
    /**
     * @notice Inicia proceso de resolución con AI oracle
     * @param _marketId ID del mercado
     */
    function initiateResolution(uint256 _marketId) external {
        Market storage market = markets[_marketId];
        if (market.status != MarketStatus.Active) revert Errors.MarketNotActive();
        if (block.timestamp < market.resolutionTime) revert Errors.InvalidTime();
        
        market.status = MarketStatus.Resolving;
        
        // Trigger AI oracle (Chainlink Functions) con Pyth price ID
        aiOracle.requestResolution(_marketId, market.question, market.pythPriceId);
    }
    
    /**
     * @notice Callback desde AI Oracle con resultado
     * @param _marketId ID del mercado
     * @param _outcome Resultado (Yes/No/Invalid)
     * @param _confidence Confianza del consenso (0-100)
     */
    function resolveMarket(
        uint256 _marketId,
        Outcome _outcome,
        uint8 _confidence
    ) external {
        if (msg.sender != address(aiOracle)) revert Errors.UnauthorizedResolver();
        
        Market storage market = markets[_marketId];
        if (market.status != MarketStatus.Resolving) revert Errors.MarketNotActive();
        
        // Si confidence < 80%, activar insurance
        if (_confidence < 80) {
            market.status = MarketStatus.Disputed;
            insurancePool.activateInsurance(_marketId);
            return;
        }
        
        market.outcome = _outcome;
        market.status = MarketStatus.Resolved;
        
        emit MarketResolved(_marketId, _outcome, block.timestamp);
    }
    
    // ============ Claim Functions ============
    
    /**
     * @notice Reclama ganancias después de resolución
     * @param _marketId ID del mercado
     */
    function claimWinnings(uint256 _marketId) external nonReentrant {
        Market storage market = markets[_marketId];
        if (market.status != MarketStatus.Resolved) revert Errors.MarketNotActive();
        
        Position storage position = positions[_marketId][msg.sender];
        if (position.claimed) revert Errors.AlreadyClaimed();
        if (position.yesShares == 0 && position.noShares == 0) revert Errors.InsufficientBalance();
        
        uint256 payout = 0;
        
        if (market.outcome == Outcome.Yes && position.yesShares > 0) {
            // Ganadores YES: proporción del pool total
            unchecked {
                payout = (position.yesShares * (market.yesPool + market.noPool)) / 
                         market.totalYesShares;
            }
        } else if (market.outcome == Outcome.No && position.noShares > 0) {
            // Ganadores NO
            unchecked {
                payout = (position.noShares * (market.yesPool + market.noPool)) / 
                         market.totalNoShares;
            }
        } else if (market.outcome == Outcome.Invalid) {
            // Refund en caso invalid
            uint256 yesInvested;
            uint256 noInvested;
            unchecked {
                yesInvested = (position.yesShares * position.avgYesPrice) / 1e18;
                noInvested = (position.noShares * position.avgNoPrice) / 1e18;
                payout = yesInvested + noInvested;
            }
        }
        
        if (payout == 0) revert Errors.InsufficientBalance();
        
        position.claimed = true;
        
        bool success = bettingToken.transfer(msg.sender, payout);
        if (!success) revert Errors.TransferFailed();
        
        emit WinningsClaimed(_marketId, msg.sender, payout);
    }
    
    /**
     * @notice Reclama seguro si oracle falló
     * @param _marketId ID del mercado
     */
    function claimInsurance(uint256 _marketId) external nonReentrant {
        Market storage market = markets[_marketId];
        if (market.status != MarketStatus.Disputed) revert Errors.MarketNotActive();
        
        Position storage position = positions[_marketId][msg.sender];
        if (position.claimed) revert Errors.AlreadyClaimed();
        
        uint256 invested = _calculateInvested(position);
        if (invested == 0) revert Errors.InsufficientBalance();
        
        position.claimed = true;
        
        // Insurance pool reembolsa inversión completa
        uint256 insurancePayout = insurancePool.processClaim(
            _marketId,
            msg.sender,
            invested
        );
        
        emit InsuranceClaimed(_marketId, msg.sender, insurancePayout);
    }
    
    // ============ View Functions ============
    
    function getMarket(uint256 _marketId) 
        external 
        view 
        returns (Market memory) 
    {
        return markets[_marketId];
    }
    
    function getPosition(uint256 _marketId, address _user) 
        external 
        view 
        returns (Position memory) 
    {
        return positions[_marketId][_user];
    }
    
    function getUserMarkets(address _user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userMarkets[_user];
    }
    
    function getCurrentOdds(uint256 _marketId) 
        external 
        view 
        returns (uint256 yesOdds, uint256 noOdds) 
    {
        Market storage market = markets[_marketId];
        
        uint256 totalPool = market.yesPool + market.noPool;
        if (totalPool == 0) return (5000, 5000); // 50/50
        
        unchecked {
            yesOdds = (market.yesPool * 10000) / totalPool;
            noOdds = (market.noPool * 10000) / totalPool;
        }
    }
    
    // ============ Internal Functions ============
    
    function _calculateInvested(Position storage position) 
        internal 
        view 
        returns (uint256) 
    {
        uint256 yesInvested;
        uint256 noInvested;
        unchecked {
            yesInvested = (position.yesShares * position.avgYesPrice) / 1e18;
            noInvested = (position.noShares * position.avgNoPrice) / 1e18;
        }
        return yesInvested + noInvested;
    }
    
    // ============ Admin Functions ============
    
    function pause() external onlyOwner {
        _pause();
    }
    
    function unpause() external onlyOwner {
        _unpause();
    }
    
    function emergencyWithdraw(address _token, uint256 _amount) 
        external 
        onlyOwner 
    {
        IERC20(_token).transfer(owner(), _amount);
    }
}

contracts/markets/SubjectiveMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SubjectiveMarket
 * @notice Mercados subjetivos resueltos por expertos via DAO voting
 * @dev Requiere expertise verification y quadratic voting
 * @dev Usa BNB nativo en lugar de tokens ERC20
 */
contract SubjectiveMarket is Ownable, ReentrancyGuard {
    address public immutable coreContract;
    address public immutable daoGovernance;
    
    struct Market {
        string question;
        string description;
        uint256 resolutionTime;
        string expertiseRequired; // "film critics", "economists", etc.
        uint256 totalYesShares;
        uint256 totalNoShares;
        uint256 yesPool;
        uint256 noPool;
        bool votingStarted;
        bool resolved;
        uint8 outcome;
    }
    
    struct Position {
        uint256 yesShares;
        uint256 noShares;
        uint256 avgYesPrice;
        uint256 avgNoPrice;
        bool claimed;
    }
    
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => Position)) public positions;
    
    event SubjectiveMarketCreated(
        uint256 indexed marketId,
        string expertiseRequired
    );
    
    event VotingStarted(
        uint256 indexed marketId,
        uint256 startTime
    );
    
    event BetPlaced(
        uint256 indexed marketId,
        address indexed user,
        bool isYes,
        uint256 amount,
        uint256 shares
    );
    
    event MarketResolved(
        uint256 indexed marketId,
        uint8 outcome
    );
    
    constructor(
        address _coreContract,
        address _daoGovernance
    ) Ownable(msg.sender) {
        coreContract = _coreContract;
        daoGovernance = _daoGovernance;
    }
    
    modifier onlyCore() {
        require(msg.sender == coreContract, "Only core");
        _;
    }
    
    modifier onlyDAO() {
        require(msg.sender == daoGovernance, "Only DAO");
        _;
    }
    
    function createMarket(
        uint256 _marketId,
        string calldata _question,
        string calldata _description,
        uint256 _resolutionTime,
        string calldata _expertiseRequired,
        string calldata // _metadata
    ) external onlyCore {
        markets[_marketId] = Market({
            question: _question,
            description: _description,
            resolutionTime: _resolutionTime,
            expertiseRequired: _expertiseRequired,
            totalYesShares: 0,
            totalNoShares: 0,
            yesPool: 0,
            noPool: 0,
            votingStarted: false,
            resolved: false,
            outcome: 0
        });
        
        emit SubjectiveMarketCreated(_marketId, _expertiseRequired);
    }
    
    function placeBet(
        uint256 _marketId,
        address _user,
        bool _isYes,
        uint256 _amount
    ) external payable onlyCore nonReentrant {
        Market storage market = markets[_marketId];
        require(!market.resolved, "Already resolved");
        require(!market.votingStarted, "Voting started");
        require(msg.value == _amount, "Amount mismatch");
        
        uint256 shares = _calculateShares(market, _isYes, _amount);
        uint256 avgPrice = (_amount * 1e18) / shares;
        
        if (_isYes) {
            market.yesPool += _amount;
            market.totalYesShares += shares;
            positions[_marketId][_user].yesShares += shares;
            positions[_marketId][_user].avgYesPrice = _calculateAvgPrice(
                positions[_marketId][_user].avgYesPrice,
                positions[_marketId][_user].yesShares - shares,
                avgPrice,
                shares
            );
        } else {
            market.noPool += _amount;
            market.totalNoShares += shares;
            positions[_marketId][_user].noShares += shares;
            positions[_marketId][_user].avgNoPrice = _calculateAvgPrice(
                positions[_marketId][_user].avgNoPrice,
                positions[_marketId][_user].noShares - shares,
                avgPrice,
                shares
            );
        }
        
        emit BetPlaced(_marketId, _user, _isYes, _amount, shares);
    }
    
    function startVoting(uint256 _marketId) external onlyDAO {
        Market storage market = markets[_marketId];
        require(!market.votingStarted, "Already started");
        require(block.timestamp >= market.resolutionTime, "Not ready");
        
        market.votingStarted = true;
        
        emit VotingStarted(_marketId, block.timestamp);
    }
    
    function resolveMarket(uint256 _marketId, uint8 _outcome) 
        external 
        onlyDAO 
    {
        Market storage market = markets[_marketId];
        require(market.votingStarted, "Voting not started");
        require(!market.resolved, "Already resolved");
        
        market.resolved = true;
        market.outcome = _outcome;
        
        emit MarketResolved(_marketId, _outcome);
    }
    
    function claimWinnings(uint256 _marketId) 
        external 
        nonReentrant 
    {
        Market storage market = markets[_marketId];
        require(market.resolved, "Not resolved");
        
        Position storage position = positions[_marketId][msg.sender];
        require(!position.claimed, "Already claimed");
        
        uint256 payout = 0;
        
        if (market.outcome == 1 && position.yesShares > 0) {
            payout = (position.yesShares * (market.yesPool + market.noPool)) / 
                     market.totalYesShares;
        } else if (market.outcome == 2 && position.noShares > 0) {
            payout = (position.noShares * (market.yesPool + market.noPool)) / 
                     market.totalNoShares;
        } else if (market.outcome == 3) {
            uint256 yesInvested = (position.yesShares * position.avgYesPrice) / 1e18;
            uint256 noInvested = (position.noShares * position.avgNoPrice) / 1e18;
            payout = yesInvested + noInvested;
        }
        
        require(payout > 0, "No winnings");
        
        position.claimed = true;
        
        (bool success, ) = payable(msg.sender).call{value: payout}("");
        require(success, "Transfer failed");
    }
    
    // Allow contract to receive BNB
    receive() external payable {}
    
    function _calculateShares(
        Market storage market,
        bool _isYes,
        uint256 _amount
    ) internal view returns (uint256) {
        uint256 pool = _isYes ? market.yesPool : market.noPool;
        uint256 totalShares = _isYes ? market.totalYesShares : market.totalNoShares;
        
        if (totalShares == 0) return _amount;
        return (_amount * totalShares) / pool;
    }
    
    function _calculateAvgPrice(
        uint256 _oldAvg,
        uint256 _oldShares,
        uint256 _newPrice,
        uint256 _newShares
    ) internal pure returns (uint256) {
        if (_oldShares == 0) return _newPrice;
        return ((_oldAvg * _oldShares) + (_newPrice * _newShares)) / 
               (_oldShares + _newShares);
    }
    
    function getMarket(uint256 _marketId) 
        external 
        view 
        returns (Market memory) 
    {
        return markets[_marketId];
    }
    
    function getPosition(uint256 _marketId, address _user) 
        external 
        view 
        returns (Position memory) 
    {
        return positions[_marketId][_user];
    }
}

contracts/mocks/MaliciousReentrancyContr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "../core/PredictionMarketCore.sol";

/**
 * @title MaliciousReentrancyContract
 * @notice Contract used for testing reentrancy protection
 * @dev This contract attempts to exploit reentrancy vulnerabilities
 */
contract MaliciousReentrancyContract {
    PredictionMarketCore public target;
    uint256 public marketId;
    bool public attacking;

    constructor(address _target) {
        target = PredictionMarketCore(payable(_target));
    }

    function attack(uint256 _marketId) external payable {
        marketId = _marketId;
        attacking = true;
        
        // Attempt to place bet and reenter
        target.placeBet{value: msg.value}(_marketId, true);
    }

    receive() external payable {
        if (attacking) {
            attacking = false;
            // Attempt reentrancy attack
            try target.placeBet(marketId, true) {} catch {}
        }
    }
}

contracts/oracle/AIOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@chainlink/contracts/src/v0.8/functions/dev/v1_0_0/FunctionsClient.sol";
import "@chainlink/contracts/src/v0.8/shared/access/OwnerIsCreator.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "../markets/PredictionMarket.sol";
import "../libraries/Errors.sol";

/**
 * @title AIOracle
 * @notice Resuelve mercados consultando backend vía Chainlink Functions
 * @dev Backend ejecuta LLM consensus, Functions solo llama backend
 */
contract AIOracle is FunctionsClient, OwnerIsCreator {
    using FunctionsRequest for FunctionsRequest.Request;
    
    // ============ State Variables ============
    
    address public predictionMarket;
    bytes32 public donId; // Chainlink DON ID
    uint64 public subscriptionId;
    uint32 public gasLimit = 300_000;
    
    // Backend URL (Vercel/Next.js API endpoint)
    string public backendUrl;
    
    mapping(bytes32 => uint256) public requestToMarketId;
    mapping(uint256 => OracleResult) public results;
    
    struct OracleResult {
        bool resolved;
        uint8 yesVotes;
        uint8 noVotes;
        uint8 invalidVotes;
        uint8 confidence;
        uint256 timestamp;
    }
    
    // ============ Events ============
    
    event ResolutionRequested(
        bytes32 indexed requestId,
        uint256 indexed marketId,
        string question
    );
    
    event ResolutionFulfilled(
        bytes32 indexed requestId,
        uint256 indexed marketId,
        uint8 outcome,
        uint8 confidence
    );
    
    // ============ Constructor ============
    
    constructor(
        address _router,
        bytes32 _donId,
        uint64 _subscriptionId,
        string memory _backendUrl
    ) FunctionsClient(_router) {
        donId = _donId;
        subscriptionId = _subscriptionId;
        backendUrl = _backendUrl;
    }
    
    // ============ Core Functions ============
    
    function setPredictionMarket(address _market) external onlyOwner {
        if (_market == address(0)) revert Errors.InvalidAmount();
        predictionMarket = _market;
    }
    
    function setBackendUrl(string memory _backendUrl) external onlyOwner {
        backendUrl = _backendUrl;
    }
    
    /**
     * @notice Solicita resolución multi-AI vía backend
     * @param _marketId ID del mercado
     * @param _question Pregunta a resolver
     * @param _pythPriceId ID de precio Pyth para validación (opcional)
     */
    function requestResolution(
        uint256 _marketId,
        string calldata _question,
        uint256 _pythPriceId
    ) external returns (bytes32) {
        if (msg.sender != predictionMarket) revert Errors.UnauthorizedResolver();
        
        // ✅ FIX #1: JavaScript source que llama AL BACKEND, no APIs directamente
        string memory source = string(abi.encodePacked(
            "const backendUrl = '", backendUrl, "';",
            "const question = args[0];",
            "const priceId = args[1];",
            "",
            "// Call backend API que ejecuta LLM consensus",
            "const response = await Functions.makeHttpRequest({",
            "  url: backendUrl,",
            "  method: 'POST',",
            "  headers: {",
            "    'Content-Type': 'application/json',",
            "    'x-chainlink-signature': Functions.env('CHAINLINK_SIGNATURE')",
            "  },",
            "  data: JSON.stringify({",
            "    marketDescription: question,",
            "    priceId: priceId",
            "  })",
            "});",
            "",
            "if (response.error) {",
            "  throw new Error('Backend request failed');",
            "}",
            "",
            "const data = response.data;",
            "",
            "// Backend retorna: { outcome, confidence, consensusCount }",
            "// outcome: 1=Yes, 2=No, 3=Invalid",
            "const outcome = data.outcome;",
            "const confidence = data.confidence;",
            "",
            "return Functions.encodeUint256(outcome * 1000 + confidence);"
        ));
        
        FunctionsRequest.Request memory req;
        req.initializeRequest(
            FunctionsRequest.Location.Inline,
            FunctionsRequest.CodeLanguage.JavaScript,
            source
        );
        
        string[] memory args = new string[](2);
        args[0] = _question;
        args[1] = Strings.toString(_pythPriceId);
        req.setArgs(args);
        
        // In test environment, if router is ZeroAddress, skip Chainlink Functions call
        // and return a zero requestId (will be handled by fulfillResolutionManual)
        bytes32 requestId;
        if (address(i_router) == address(0)) {
            requestId = bytes32(0);
            requestToMarketId[requestId] = _marketId;
            emit ResolutionRequested(requestId, _marketId, _question);
            return requestId;
        }
        
        requestId = _sendRequest(
            req.encodeCBOR(),
            subscriptionId,
            gasLimit,
            donId
        );
        
        requestToMarketId[requestId] = _marketId;
        
        emit ResolutionRequested(requestId, _marketId, _question);
        
        return requestId;
    }
    
    /**
     * @notice Callback de Chainlink Functions con respuesta del backend
     */
    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        uint256 marketId = requestToMarketId[requestId];
        if (marketId == 0) revert Errors.InvalidRequestId();
        
        if (err.length > 0) {
            // Error: marcar como invalid
            results[marketId] = OracleResult({
                resolved: true,
                yesVotes: 0,
                noVotes: 0,
                invalidVotes: 5,
                confidence: 0,
                timestamp: block.timestamp
            });
            
            PredictionMarket(payable(predictionMarket)).resolveMarket(
                marketId,
                PredictionMarket.Outcome.Invalid,
                0
            );
            return;
        }
        
        uint256 result = abi.decode(response, (uint256));
        uint8 outcome = uint8(result / 1000);
        uint8 confidence = uint8(result % 1000);
        
        // outcome: 1=Yes, 2=No, 3=Invalid
        uint8 yesVotes = outcome == 1 ? 5 : 0;
        uint8 noVotes = outcome == 2 ? 5 : 0;
        uint8 invalidVotes = outcome == 3 ? 5 : 0;
        
        results[marketId] = OracleResult({
            resolved: true,
            yesVotes: yesVotes,
            noVotes: noVotes,
            invalidVotes: invalidVotes,
            confidence: confidence,
            timestamp: block.timestamp
        });
        
        PredictionMarket.Outcome finalOutcome;
        if (outcome == 1) finalOutcome = PredictionMarket.Outcome.Yes;
        else if (outcome == 2) finalOutcome = PredictionMarket.Outcome.No;
        else finalOutcome = PredictionMarket.Outcome.Invalid;
        
        PredictionMarket(payable(predictionMarket)).resolveMarket(
            marketId,
            finalOutcome,
            confidence
        );
        
        emit ResolutionFulfilled(requestId, marketId, outcome, confidence);
    }
    
    /**
     * @notice Resuelve un mercado manualmente sin Chainlink Functions (para Gelato Bot)
     * @dev Permite resolver mercados cuando Chainlink Functions no está disponible
     * @param _marketId ID del mercado a resolver
     * @param _outcome Resultado del consenso (1=Yes, 2=No, 3=Invalid)
     * @param _confidence Nivel de confianza del consenso (0-100)
     */
    function fulfillResolutionManual(
        uint256 _marketId,
        uint8 _outcome,
        uint8 _confidence
    ) external onlyOwner {
        if (_marketId == 0) revert Errors.InvalidAmount();
        if (_outcome < 1 || _outcome > 3) revert Errors.InvalidAmount();
        if (_confidence > 100) revert Errors.InvalidAmount();
        
        // Verificar que el mercado no esté ya resuelto
        if (results[_marketId].resolved) {
            revert Errors.InvalidRequestId(); // Reutilizar error para "ya resuelto"
        }
        
        // outcome: 1=Yes, 2=No, 3=Invalid
        uint8 yesVotes = _outcome == 1 ? 5 : 0;
        uint8 noVotes = _outcome == 2 ? 5 : 0;
        uint8 invalidVotes = _outcome == 3 ? 5 : 0;
        
        // Guardar resultado
        results[_marketId] = OracleResult({
            resolved: true,
            yesVotes: yesVotes,
            noVotes: noVotes,
            invalidVotes: invalidVotes,
            confidence: _confidence,
            timestamp: block.timestamp
        });
        
        // Convertir outcome a enum de PredictionMarket
        PredictionMarket.Outcome finalOutcome;
        if (_outcome == 1) finalOutcome = PredictionMarket.Outcome.Yes;
        else if (_outcome == 2) finalOutcome = PredictionMarket.Outcome.No;
        else finalOutcome = PredictionMarket.Outcome.Invalid;
        
        // Resolver mercado en PredictionMarket
        PredictionMarket(payable(predictionMarket)).resolveMarket(
            _marketId,
            finalOutcome,
            _confidence
        );
        
        // Emitir evento (usar requestId cero para indicar resolución manual)
        emit ResolutionFulfilled(bytes32(0), _marketId, _outcome, _confidence);
    }
    
    // ============ View Functions ============
    
    function getResult(uint256 _marketId) 
        external 
        view 
        returns (OracleResult memory) 
    {
        return results[_marketId];
    }
}

contracts/oracle/InsurancePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title InsurancePool
 * @notice Pool de seguro con yield farming (BNB nativo)
 * @dev Protege contra fallos del oracle, genera yield en OPBNB
 * @dev Usa BNB nativo en lugar de tokens ERC20
 */
contract InsurancePool is Ownable, ReentrancyGuard {
    // ============ State Variables ============
    
    address public coreContract;
    
    uint256 public totalInsured;
    uint256 public totalClaimed;
    uint256 public totalYieldGenerated;
    uint256 public utilizationRateBP; // basis points
    uint256 public maxUtilization = 8000; // 80% max
    
    // Insurance parameters
    uint256 public constant PREMIUM_FEE_BP = 10; // 0.1%
    uint256 public constant MIN_DEPOSIT = 0.01 ether; // 0.01 BNB
    uint256 public constant CLAIM_COOLDOWN = 1 hours;
    
    // Share-based system (similar to ERC4626 but for native BNB)
    uint256 public totalShares;
    mapping(address => uint256) public shares;
    
    struct InsurancePolicy {
        uint256 marketId;
        bool activated;
        uint256 reserve;
        uint256 claimed;
        uint256 activatedAt;
        uint256 expiresAt;
        mapping(address => bool) hasClaimed;
    }
    
    struct UserDeposit {
        uint256 amount;
        uint256 shares;
        uint256 depositedAt;
        uint256 lastYieldClaim;
    }
    
    mapping(uint256 => InsurancePolicy) public policies;
    mapping(address => UserDeposit) public deposits;
    mapping(address => uint256[]) public userPolicies;
    
    // Yield tracking
    uint256 public totalYieldAccrued;
    uint256 public lastYieldHarvest;
    uint256 public yieldPerShare; // scaled by 1e18
    
    // ============ Events ============
    
    event Deposited(
        address indexed user,
        uint256 assets,
        uint256 shares
    );
    
    event Withdrawn(
        address indexed user,
        uint256 assets,
        uint256 shares
    );
    
    event InsuranceActivated(
        uint256 indexed marketId,
        uint256 reserve,
        uint256 expiresAt
    );
    
    event ClaimProcessed(
        uint256 indexed marketId,
        address indexed user,
        uint256 amount
    );
    
    event YieldHarvested(
        uint256 amount,
        uint256 timestamp
    );
    
    event YieldDistributed(
        address indexed user,
        uint256 amount
    );
    
    // ============ Constructor ============
    
    constructor() Ownable(msg.sender) {
        lastYieldHarvest = block.timestamp;
    }
    
    // ============ Modifiers ============
    
    modifier onlyCore() {
        require(msg.sender == coreContract, "Only core");
        _;
    }
    
    // ============ Admin Functions ============
    
    function setCoreContract(address _core) external onlyOwner {
        coreContract = _core;
    }
    
    function setMaxUtilization(uint256 _maxBP) external onlyOwner {
        require(_maxBP <= 10000, "Invalid BP");
        maxUtilization = _maxBP;
    }
    
    // ============ Deposit/Withdraw Functions ============
    
    /**
     * @notice Deposita BNB para ganar yield + proteger mercados
     */
    function deposit(address receiver) 
        external 
        payable 
        nonReentrant 
        returns (uint256 userShares) 
    {
        require(msg.value >= MIN_DEPOSIT, "Below min deposit");
        
        // Harvest yield before deposit
        _harvestYield();
        
        // Calculate shares (1:1 initially, can be adjusted for yield)
        userShares = _convertToShares(msg.value);
        
        totalShares += userShares;
        shares[receiver] += userShares;
        
        deposits[receiver] = UserDeposit({
            amount: msg.value,
            shares: shares[receiver],
            depositedAt: block.timestamp,
            lastYieldClaim: block.timestamp
        });
        
        emit Deposited(receiver, msg.value, userShares);
        
        return userShares;
    }
    
    /**
     * @notice Retira BNB + yield acumulado
     */
    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    ) external nonReentrant returns (uint256 userShares) {
        require(msg.sender == owner || msg.sender == coreContract, "Unauthorized");
        
        // Claim pending yield first
        _claimYield(owner);
        
        // Check utilization
        uint256 available = totalAssets() - totalInsured + totalClaimed;
        require(assets <= available, "Insufficient liquidity");
        
        userShares = _convertToShares(assets);
        require(shares[owner] >= userShares, "Insufficient shares");
        
        totalShares -= userShares;
        shares[owner] -= userShares;
        
        // Update deposits
        if (deposits[owner].amount > 0) {
            if (deposits[owner].amount <= assets) {
                delete deposits[owner];
            } else {
                deposits[owner].amount -= assets;
                deposits[owner].shares -= userShares;
            }
        }
        
        // Transfer BNB
        (bool success, ) = payable(receiver).call{value: assets}("");
        require(success, "Transfer failed");
        
        emit Withdrawn(owner, assets, userShares);
        
        return userShares;
    }
    
    // ============ Insurance Functions ============
    
    /**
     * @notice Recibe premium de insurance de apuestas
     */
    function receiveInsurancePremium(
        uint256 _marketId,
        uint256 _amount
    ) external payable onlyCore {
        require(msg.value >= _amount, "Insufficient BNB");
        
        policies[_marketId].reserve += _amount;
        totalInsured += _amount;
        
        // Update utilization
        uint256 total = totalAssets();
        if (total > 0) {
            utilizationRateBP = (totalInsured * 10000) / total;
        }
    }
    
    /**
     * @notice Activa seguro cuando oracle falla
     */
    function activateInsurance(uint256 _marketId) external onlyCore {
        InsurancePolicy storage policy = policies[_marketId];
        require(!policy.activated, "Already activated");
        require(policy.reserve > 0, "No reserve");
        
        policy.activated = true;
        policy.marketId = _marketId;
        policy.activatedAt = block.timestamp;
        policy.expiresAt = block.timestamp + 30 days;
        
        emit InsuranceActivated(_marketId, policy.reserve, policy.expiresAt);
    }
    
    /**
     * @notice Procesa claim de seguro
     */
    function processClaim(
        uint256 _marketId,
        address _user,
        uint256 _amount
    ) external onlyCore nonReentrant returns (uint256) {
        InsurancePolicy storage policy = policies[_marketId];
        require(policy.activated, "Not activated");
        require(!policy.hasClaimed[_user], "Already claimed");
        require(block.timestamp <= policy.expiresAt, "Policy expired");
        require(
            block.timestamp >= policy.activatedAt + CLAIM_COOLDOWN,
            "Cooldown period"
        );
        
        // Verify sufficient funds
        uint256 available = totalAssets() - totalInsured + totalClaimed;
        require(available >= _amount, "Insufficient funds");
        
        // Cap claim to reserve
        uint256 claimAmount = _amount;
        if (policy.claimed + claimAmount > policy.reserve) {
            claimAmount = policy.reserve - policy.claimed;
        }
        
        policy.hasClaimed[_user] = true;
        policy.claimed += claimAmount;
        totalClaimed += claimAmount;
        
        // Update utilization
        uint256 total = totalAssets();
        if (total > 0) {
            utilizationRateBP = ((totalInsured - totalClaimed) * 10000) / total;
        }
        
        // Transfer BNB
        (bool success, ) = payable(_user).call{value: claimAmount}("");
        require(success, "Transfer failed");
        
        emit ClaimProcessed(_marketId, _user, claimAmount);
        
        return claimAmount;
    }
    
    // ============ Yield Functions ============
    
    /**
     * @notice Harvest yield (simplified - can be extended with staking)
     */
    function _harvestYield() internal {
        if (block.timestamp < lastYieldHarvest + 1 hours) return;
        
        // Simple yield calculation (can be extended with actual staking)
        // For now, we'll use a simple interest rate
        uint256 timeElapsed = block.timestamp - lastYieldHarvest;
        uint256 baseYield = (totalAssets() * timeElapsed * 500) / (10000 * 365 days); // ~5% APY
        
        if (baseYield > 0 && totalShares > 0) {
            totalYieldGenerated += baseYield;
            totalYieldAccrued += baseYield;
            yieldPerShare += (baseYield * 1e18) / totalShares;
            lastYieldHarvest = block.timestamp;
            
            emit YieldHarvested(baseYield, block.timestamp);
        }
    }
    
    /**
     * @notice Reclama yield acumulado
     */
    function claimYield() external nonReentrant {
        _claimYield(msg.sender);
    }
    
    function _claimYield(address _user) internal {
        UserDeposit storage userDeposit = deposits[_user];
        if (userDeposit.shares == 0) return;
        
        _harvestYield();
        
        // Calculate pending yield
        uint256 pendingYield = (userDeposit.shares * yieldPerShare) / 1e18;
        uint256 lastClaimed = (userDeposit.shares * 
            (yieldPerShare - ((block.timestamp - userDeposit.lastYieldClaim) * 1e18 / 365 days))
        ) / 1e18;
        
        uint256 claimable = pendingYield > lastClaimed ? pendingYield - lastClaimed : 0;
        
        if (claimable > 0) {
            userDeposit.lastYieldClaim = block.timestamp;
            
            (bool success, ) = payable(_user).call{value: claimable}("");
            require(success, "Transfer failed");
            
            emit YieldDistributed(_user, claimable);
        }
    }
    
    // ============ Internal Functions ============
    
    function _convertToShares(uint256 assets) internal view returns (uint256) {
        if (totalShares == 0 || totalAssets() == 0) {
            return assets; // 1:1 initially
        }
        return (assets * totalShares) / totalAssets();
    }
    
    function _convertToAssets(uint256 _shares) internal view returns (uint256) {
        if (totalShares == 0) return 0;
        return (_shares * totalAssets()) / totalShares;
    }
    
    function totalAssets() public view returns (uint256) {
        return address(this).balance;
    }
    
    // ============ View Functions ============
    
    function getPoolHealth() external view returns (
        uint256 totalAsset,
        uint256 insured,
        uint256 claimed,
        uint256 available,
        uint256 utilizationRate,
        uint256 yieldAPY
    ) {
        totalAsset = totalAssets();
        insured = totalInsured;
        claimed = totalClaimed;
        available = totalAsset - insured + claimed;
        utilizationRate = utilizationRateBP;
        
        // Calculate APY (simplified)
        if (block.timestamp > lastYieldHarvest && totalAsset > 0) {
            uint256 timeElapsed = block.timestamp - lastYieldHarvest;
            uint256 yearlyYield = (totalYieldGenerated * 365 days) / (timeElapsed + 1);
            yieldAPY = (yearlyYield * 10000) / totalAsset;
        }
    }
    
    function getPolicyStatus(uint256 _marketId) 
        external 
        view 
        returns (
            bool activated,
            uint256 reserve,
            uint256 claimed,
            uint256 expiresAt,
            bool expired
        ) 
    {
        InsurancePolicy storage policy = policies[_marketId];
        activated = policy.activated;
        reserve = policy.reserve;
        claimed = policy.claimed;
        expiresAt = policy.expiresAt;
        expired = block.timestamp > policy.expiresAt;
    }
    
    function getUserDeposit(address _user) 
        external 
        view 
        returns (UserDeposit memory) 
    {
        return deposits[_user];
    }
    
    function getPendingYield(address _user) 
        external 
        view 
        returns (uint256) 
    {
        UserDeposit storage userDeposit = deposits[_user];
        if (userDeposit.shares == 0) return 0;
        
        uint256 pendingYield = (userDeposit.shares * yieldPerShare) / 1e18;
        uint256 lastClaimed = (userDeposit.shares * 
            (yieldPerShare - ((block.timestamp - userDeposit.lastYieldClaim) * 1e18 / 365 days))
        ) / 1e18;
        
        return pendingYield > lastClaimed ? pendingYield - lastClaimed : 0;
    }
    
    function hasClaimed(uint256 _marketId, address _user) 
        external 
        view 
        returns (bool) 
    {
        return policies[_marketId].hasClaimed[_user];
    }
    
    // Allow contract to receive BNB
    receive() external payable {}
}

contracts/reputation/ReputationDAO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

// TODO: Re-enable CCIP when Chainlink 0.8.0+ is available
// import "@chainlink/contracts/src/v0.8/ccip/client/CCIPReceiver.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ReputationDAO
 * @notice Cross-protocol reputation staking with economic penalties
 * @dev Slash amount = Base Stake × (Market Size / $1M) × (1 - Reputation Score)
 * @dev CCIP integration temporarily disabled for initial deployment
 */
contract ReputationDAO is Ownable {
    struct ReputationScore {
        uint256 stake;
        uint256 accuracy; // 0-100
        uint256 disputesWon;
        uint256 slashesIncurred;
        bool isMember;
        uint256 joinedAt;
    }

    struct DisputeParticipation {
        uint256 marketId;
        uint256 confidence; // 0-100
        bool wasCorrect;
        uint256 timestamp;
    }

    mapping(address => ReputationScore) public reputationScores;
    mapping(address => DisputeParticipation[]) public participationHistory;
    mapping(address => bool) public isSlashed;

    uint256 public minimumStake = 100e18; // 100 USDC
    uint256 public slashingPercentage = 20; // 20% base

    event MemberAdmitted(address indexed user, uint256 stake);
    event ReputationUpdated(address indexed user, uint256 accuracy);
    event StakesSlashed(
        address indexed user,
        uint256 amount,
        uint256 marketSize
    );
    event ReputationPortedCrossChain(
        address indexed user,
        uint256 score,
        uint256 chainId
    );

    // TODO: Re-enable CCIP router when available
    // constructor(address _router) CCIPReceiver(_router) Ownable(msg.sender) {}
    constructor() Ownable(msg.sender) {}

    /**
     * @notice Join reputation DAO with stake
     */
    function joinDAO(uint256 _stakeAmount) external {
        require(_stakeAmount >= minimumStake, "Stake too low");
        require(
            !reputationScores[msg.sender].isMember,
            "Already member"
        );

        // In production: Transfer _stakeAmount USDC to contract
        // IERC20(usdcAddress).transferFrom(msg.sender, address(this), _stakeAmount);

        reputationScores[msg.sender] = ReputationScore({
            stake: _stakeAmount,
            accuracy: 50, // Start at 50%
            disputesWon: 0,
            slashesIncurred: 0,
            isMember: true,
            joinedAt: block.timestamp
        });

        emit MemberAdmitted(msg.sender, _stakeAmount);
    }

    /**
     * @notice Update reputation after dispute resolution
     */
    function updateReputation(
        address _user,
        bool _wasCorrect,
        uint256 _marketSize,
        uint256 _confidence
    ) external onlyOwner {
        require(reputationScores[_user].isMember, "Not member");

        ReputationScore storage score = reputationScores[_user];

        participationHistory[_user].push(
            DisputeParticipation({
                marketId: 0, // Would come from market ID
                confidence: _confidence,
                wasCorrect: _wasCorrect,
                timestamp: block.timestamp
            })
        );

        if (_wasCorrect) {
            score.accuracy = (score.accuracy * 3 + 100) / 4; // Bayesian update
            score.disputesWon++;
        } else {
            score.accuracy = (score.accuracy * 3 + 0) / 4; // Bayesian update down
            _slashStake(_user, _marketSize, _confidence);
        }

        emit ReputationUpdated(_user, score.accuracy);
    }

    /**
     * @notice Internal: Slash stake based on market size and reputation
     */
    function _slashStake(
        address _user,
        uint256 _marketSize,
        uint256 _confidence
    ) internal {
        ReputationScore storage score = reputationScores[_user];

        // Slash formula: Base × (Market/1M) × (1 - Reputation%) × Confidence%
        uint256 marketMultiplier = _marketSize / 1e6; // Market size in millions
        if (marketMultiplier == 0) marketMultiplier = 1; // Minimum 1x

        uint256 reputationMultiplier = (100 - score.accuracy);
        uint256 slashAmount = (score.stake *
            marketMultiplier *
            reputationMultiplier *
            _confidence) / 1000000; // 100 * 100 * 100

        if (slashAmount > score.stake) {
            slashAmount = score.stake; // Cap at stake amount
        }

        score.stake -= slashAmount;
        score.slashesIncurred++;

        if (score.stake < minimumStake) {
            score.isMember = false;
            isSlashed[_user] = true;
        }

        emit StakesSlashed(_user, slashAmount, _marketSize);
    }

    /**
     * @notice Get reputation score for user
     */
    function getReputation(
        address _user
    ) external view returns (ReputationScore memory) {
        return reputationScores[_user];
    }

    /**
     * @notice Port reputation cross-chain via CCIP
     */
    function portReputationCrossChain(
        uint256 _destinationChainId
    ) external {
        ReputationScore memory score = reputationScores[msg.sender];
        require(score.isMember, "Not member");

        // In production: Call Chainlink CCIP to sync reputation
        emit ReputationPortedCrossChain(
            msg.sender,
            score.accuracy,
            _destinationChainId
        );
    }

    /**
     * @notice Handle CCIP message from other chains
